def Switch_Init(self, QMenu_MenuItems, QMenu_Menus, QMenu_MenuSets):
	if Application.Preferences.GetPreferenceValue("Select.rectraycast") == 1:
		return True
	return False


def Switch_Execute(self, QMenu_MenuItems, QMenu_Menus, QMenu_MenuSets):
	Application.SetValue("preferences.Select.rectraycast", not Switch_Init(self, QMenu_MenuItems, QMenu_Menus, QMenu_MenuSets))

