def Script_Execute (self, QMenu_MenuItems, QMenu_Menus, QMenu_MenuSets): #Dont rename this function 
	if len(Application.Selection) >0 :
		if (Application.Selection(0).Type == "crvbndrySubComponent"):
			FirstBoundary = Application.Selection(0)
		else:
			FirstResult = Application.PickElement( "CurveBoundary" , "Select first Boundary", "Select first Boundary", "", "", 0, "" )
			Application.LogMessage("Past first pick session A")
			FirstBoundary = FirstResult.Value("PickedElement")
	else:
		FirstResult = Application.PickElement( "CurveBoundary" , "Select first Boundary", "Select first Boundary", "", "", 0, "" )
		Application.LogMessage("Past first pick session B")
		FirstBoundary = FirstResult.Value("PickedElement")
	
	if len(Application.Selection) >1:
		if (Application.Selection(1).Type == "crvbndrySubComponent"):
			SecondBoundary = Application.Selection(1)
		else:
			SecondResult = Application.PickElement( "CurveBoundary" , "Select second Boundary", "Select second Boundary", "", "", 0, "" )
			Application.LogMessage("Past second pick session A.")
			SecondBoundary = SecondResult.Value("PickedElement")
	else:
		SecondResult = Application.PickElement( "CurveBoundary" , "Select second Boundary", "Select second Boundary", "", "", 0, "" )
		Application.LogMessage("Past second pick session B")
		SecondBoundary = SecondResult.Value("PickedElement")
	
	if FirstBoundary.Type == "crvbndrySubComponent" and SecondBoundary.Type == "crvbndrySubComponent":
		Result = Application.ApplyGenOp("BlendCrv", "", [FirstBoundary,SecondBoundary], 3, "siPersistentOperation", "siKeepGenOpInputs", "")
		
	if Result != None:
		if Application.Preferences.GetPreferenceValue( "xsiprivate_unclassified.OperationMode" ) == True: #Is ImmediateMode enabled?
			ReturnVal = Application.InspectObj(Result,"","",4,False)
			if ReturnVal == True: #User has canceled?
				Application.DeleteObj()
			else:
				Application.FreezeModeling(Result)
		else: #We are not in ImmediateMode, this is simpler...
			keyState = Application.GetKeyboardState() #Get currently pressed modifier keys (if any)
			bCtrlDown = keyState[1] == 2 #Is Ctrl pressed?
			bAutoInspect = Application.GetUserPref( "AutoInspectEnabled" )
			if bAutoInspect and bCtrlDown==False or bAutoInspect==False and bCtrlDown:
				Application.InspectObj(Result,"","",1,False)

def Script_Execute (self, QMenu_MenuItems, QMenu_Menus, QMenu_MenuSets): #Dont rename this function 
	CustomParams = Application.FindObjects( "", "{76332571-D242-11d0-B69C-00AA003B3EA6}" )
	for CP in CustomParams:
		if CP.Type == ("QMenuConfigurator"):
			Application.InspectObj (CP)
			return

def Script_Execute (self, QMenu_MenuItems, QMenu_Menus, QMenu_MenuSets): #Dont rename this function 
	Mats = list()
	for oObj in Application.Selection:
		try:
			Mats.append(oObj.Material)
		except:
			pass

	Application.InspectObj(Mats)

