QPopMenuItems.QPopMenuItem:
def Switch_Init():
	if Application.Preferences.GetPreferenceValue("duplicate.polygons") == 0:
		return True
	return False


def Switch_Execute():
	if Switch_Init() == True:
		Application.SetValue("Preferences.duplicate.polygons",1)
	else:
		Application.SetValue("Preferences.duplicate.polygons",0)

