def Switch_Init(self, QPopMenuItems, QPopMenus, QPopMenuSets):
	if Application.Preferences.GetPreferenceValue("Select.rectraycast") == 1:
		return True
	return False


def Switch_Execute(self, QPopMenuItems, QPopMenus, QPopMenuSets):
	Application.SetValue("preferences.Select.rectraycast", not Switch_Init(self, QPopMenuItems, QPopMenus, QPopMenuSets))

def Switch_Init(self, QPopMenuItems, QPopMenus, QPopMenuSets):
	if Application.Preferences.GetPreferenceValue("Select.raycastbackfacing") == 1:
