QPopMenuItems.QPopMenuItem:
def Script_Execute(Self):
	MarkedParams = Application.GetMarking()
	if MarkedParams != None:
		if len(MarkedParams) > 0:
			for oObj in Application.Selection:
				for oParam in MarkedParams:
					try:
						FullParamName = (oObj.FullName + "." +  oParam)
						Application.LogMessage(FullParamName)
						CurrentVal = Application.GetValue(FullParamName)
						Application.LogMessage
						Application.SetValue (oObj.FullName + "." + oParam,(CurrentVal *1.1),"")
					except:
						pass
						#Application.LogMessage("Something went wrong")

