def Switch_Init():
	if Application.Preferences.GetPreferenceValue("Select.rectraycast") == 1:
		return True
	return False


def Switch_Execute():
	Application.SetValue("preferences.Select.rectraycast", not Switch_Init())

def Switch_Init():
	if Application.Preferences.GetPreferenceValue("Select.raycastbackfacing") == 1:
		return True
	return False

def Switch_Execute():
	Application.SetValue("preferences.Select.raycastbackfacing", not Switch_Init())

def Switch_Init(): #Don't rename this function State = Application.Preferences.GetPreferenceValue("Camera.orbitselected") == 2 return State
def Switch_Execute(Self): #Don't rename this function
if Application.Preferences.GetPreferenceValue("Camera.orbitselected") != 2: Application.SetValue("preferences.Camera.orbitselected", 2, "") else: Application.SetValue("preferences.Camera.orbitselected", 0, "")

def Script_Execute(Self):
	Sel = Application.Selection
	oSel = list()
	for Obj in Sel:
		oSel.append(Obj)
	 
	 
	for oObj in oSel:
		delClusters = list()
		Clusters = oObj.ActivePrimitive.Geometry.Clusters
		polyClusters = list()
		for oCluster in Clusters:
			if oCluster.Type == "poly" and len(oCluster.Elements) != 0:
				polyClusters.append (oCluster)
	 
		
		for polyCluster in polyClusters:
			currentmat = polyCluster.Material
			#pplication.LogMessage(currentmat)

			for oCheckCluster in polyClusters:
				if oCheckCluster != polyCluster and len(oCheckCluster.Elements) != 0:
					if oCheckCluster.Material.IsEqualTo(polyCluster.Material):
						#Application.LogMessage("Processing cluster: " + oCheckCluster.name)
						clusterElements = oCheckCluster.Elements
						Application.DeselectAll()
						Application.SelectMembers(oCheckCluster.FullName,True,False)
						Application.AddToSelection(polyCluster.FullName,"ASITIS",False)
						Application.AddToCluster("", "siRemoveFromExistingClusters")
		
		
		for Cluster in oObj.ActivePrimitive.Geometry.Clusters:
			if len(Cluster.Elements) == 0:
				Application.DeleteObj(Cluster.FullName)

	Application.Application.DeselectAll()
	Application.SelectObj(oSel)

