QMenu_MenuItems.QMenu_MenuItem:
def Script_Execute ( oContext ): #Don't rename this function
	Application.QMenuRefreshSelectionContextObject(9999999)
	MarkedParams = Application.GetMarking()
	if MarkedParams != None:
		if len(MarkedParams) > 0:
			for oObj in oContext.X3DObjects:
				for oParam in MarkedParams:
					FullParamName = (oObj.FullName + "." +  oParam)
					if Application.Dictionary.GetObject(FullParamName).Type == "Parameter":
						CurrentVal = Application.GetValue(FullParamName)
						Application.SetValue (oObj.FullName + "." + oParam,CurrentVal*1.1,"")
					else:
						Params = Application.Dictionary.GetObject(FullParamName)
						for Parameter in Params.Parameters:
							CurrentVal = Application.GetValue(Parameter.FullName)
							Application.SetValue (Parameter.FullName,CurrentVal*1.1,"")

