def Script_Execute (oContext): #Dont rename this function oView = oContext.CurrentXSIView if (oView != None) and (oView.Type == "ICE Tree"): ActiveIceOp = oView.GetAttributeValue("container") if ActiveIceOp != "": #value type of returned ICE container "string" keyState = Application.GetKeyboardState() #Get currently pressed modifier keys (Shift, Ctrl, Alt) ClickedMenuItemNumber = oContext.ClickedMenuItemNumber oClickedMenu = oContext.ClickedMenu
if keyState[1] == 1: #Is Shift pressed? -> Add the last created ICE/Render node to the QMenu print ("Shift key is pressed") LastCreatedNodeString = Application.QMenuGetRecentNode() LastCreatedNodeData = LastCreatedNodeString.split(";") SelectedNodeFullName = oView.GetAttributeValue("selection") oSelectedNode = Application.Dictionary.GetObject(SelectedNodeFullName) if oSelectedNode != None: SelectedNodeName = oSelectedNode.Name IceCommand = LastCreatedNodeData[0] Preset = LastCreatedNodeData[1] MenuItem = Application.GetQMenu("MenuItem","Dummy_CreateICENode") MenuItemCode = MenuItem.Code MenuItemCode = MenuItemCode.replace(("ICE" + "COMMAND"), IceCommand) MenuItemCode = MenuItemCode.replace(("PRE" + "SET"), Preset) NewMenuItem = Application.QMenuCreateObject("MenuItem") NewMenuItem.Code = MenuItemCode NewMenuItem.Name = SelectedNodeName oClickedMenu.insertMenuItem( ClickedMenuItemNumber , NewMenuItem ) else: Application.LogMessage("No Node was selected to derive the new Menu Item's name from. Create A new node, leave it selected and try again please.")
if keyState[1] == 4: #Is Alt pressed? -> Remove the clicked Menu Item from the QMenu print ("Alt key is pressed") ClickedMenu.removeMenuItem (ClickedMenuItemNumber)
if keyState[1] != 1 and keyState[1] != 4: #Neither Alt nor Shift keys are pressed, execute the menu item normally Application.ICECOMMAND ("PRESET", ActiveIceOp.FullName)

def Script_Execute (oContext): #Dont rename this function oView = oContext.CurrentXSIView if (oView != None) and (oView.Type == "ICE Tree"): ActiveIceOp = oView.GetAttributeValue("container") if ActiveIceOp != "": #value type of returned ICE container "string" keyState = Application.GetKeyboardState() #Get currently pressed modifier keys (Shift, Ctrl, Alt) ClickedMenuItemNumber = oContext.ClickedMenuItemNumber oClickedMenu = oContext.ClickedMenu
