QMenu_MenuDisplayContexts.QMenu_MenuDisplayContext:
def QMenuContext_Execute( oContext ): #This function must not be renamed!
	Types = oContext.Types
	if (oContext.Types[0] == "pntSubComponent"):
		if oContext.ComponentParentTypes[0] == "polymsh": #Is the parent of the selected component(s) a polymesh? (could be a curve too)
			return True								
	return False	#This function must return a boolean!

function QMenuContext_Execute( oContext ) //This function must not be renamed!
{
	LogMessage(oContext.ThisQMenuObject.Name);
	classNames = oContext.Types.toArray();
	LogMessage(classNames)
	//Application.LogMessage(oContext.ComponentClassNames)
	//Application.LogMessage(oContext.ComponentParents)
	//Application.LogMessage(oContext.ComponentParentTypes)
	//Application.LogMessage(oContext.ComponentParentClassNames)
	return true	//This function must return a boolean
}

def QMenuContext_Execute( oContext ): #This function must not be renamed!
	Types = oContext.Types
	if len(Types) > 1:
		if (Types[0] == "surfmsh") and (Types[1] == "surfmsh"):
			return True		
	return False #This function must return a boolean

def QMenuContext_Execute( oContext ): #This function must not be renamed!
	try:
		for prop in Application.Selection[0].Properties:
			if prop.Type == "ArnoldStandInProp":
				return True	
	except:
		return False
	return False #This function must return a boolean
